public frmSignatureCapture()
{
    InitializeComponent();
    DocSig = new DocumentCapture();

    //set ICScenarioTextEnhancement value
    DocSig.SignatureScenario1.ICScenarioTextEnhancement = ImageConditioning.TextEnhancementValue.Medium;
}

