            try
            {
                 docCamera = new DocumentCapture("Camera");
            }
            catch (DocumentCaptureException DocCapExp)
            {
                MessageBox.Show("DocumentCaptureException: " + DocCapExp.Message);
            }                      
        }
    }
}

