public frmSymbologyOptions()
{
    try
    {
        InitializeComponent();
        symbOption = new SymbologyOptions();
        symbOption.EnableSymbologyOptionsExceptions(true);
        symbOption.Preamble = "Martin";
    }
    catch (SymbologyOptionsException exp)
    {
        MessageBox.Show(exp.Message);
    }           
}

