                        symb = New CSymbology()
                        symb.Code39.fullAsciiConversion = CSymbology.CCODE39.EFullAsciiConversion.Enable
                        Dim retValue As String = symb.Code39.fullAsciiConversion.ToString()
                    End Sub
                 End Class

