					        img.SnapShotConditioning.Subsampling = ImageConditioning.SubsamplingValue.None;
					        img.SnapShotConditioning.OutputCompression = ImageConditioning.OutputCompressionValue.Bitmap;
				        }

                        //enables streaming video
